	(160, 43, '_form', '<div class="form-group">\n       <label for="exampleInputEmail1">Nome:</label>\n       [text* text-224 class:form-control]\n  </div>\n  <div class="form-group">\n     <label for="exampleInputPassword1">E-mail: </label>\n     [text* text-803 class:form-control]\n  </div>\n  <div class="form-group">\n     <label for="exampleInputPassword1">Telefone: </label>\n     [text* text-721 class:form-control]\n  </div>\n  <div class="form-group">\n     <label for="exampleInputPassword1">Descreva sua necessidade: </label>\n     [textarea* textarea-551 class:form-control]\n  </div>\n  <div class="form-group">\n     <label for="exampleInputPassword1">Insira os caracteres da figura abaixo: </label>\n     [captchac captcha-36]\n     [captchar captcha-36]\n  </div>\n  [submit class:btn class:btn-primary "Enviar"]'),
	(161, 43, '_mail', 'a:8:{s:7:"subject";s:10:"Orçamento";s:6:"sender";s:46:"[Fernando Rodrigues] <nandorodpires@gmail.com>";s:4:"body";s:300:"De: [your-name] <[your-email]>\nAssunto: [your-subject]\n\nCorpo da mensagem:\nNova solicitação de orçamento\n\nNome: [text-224]\nEmail: [text-803]\nTelefone: [text-721]\n<p>Necessidade</p>\n[textarea-551]\n--\nEste e-mail foi enviado de um formulário de contato em Stylesheets (http://localhost/stylesheets)";s:9:"recipient";s:23:"nandorodpires@gmail.com";s:18:"additional_headers";s:0:"";s:11:"attachments";s:0:"";s:8:"use_html";b:0;s:13:"exclude_blank";b:0;}'),
	(162, 43, '_mail_2', 'a:9:{s:6:"active";b:0;s:7:"subject";s:14:"[your-subject]";s:6:"sender";s:37:"Stylesheets <nandorodpires@gmail.com>";s:4:"body";s:136:"Corpo da mensagem:\n[your-message]\n\n--\nEste e-mail foi enviado de um formulário de contato em Stylesheets (http://localhost/stylesheets)";s:9:"recipient";s:12:"[your-email]";s:18:"additional_headers";s:33:"Reply-To: nandorodpires@gmail.com";s:11:"attachments";s:0:"";s:8:"use_html";b:0;s:13:"exclude_blank";b:0;}'),
